mapper.update:
update office.attachment set 
location = #{location},
type = #{type},
keyentry = #{keyentry} 
where isdel = 0 and 
id = (select attachment from office.article where isdel = 0 and id = #{id});

update office.attachment set 
isdel = 1
where id = (select attachment from office.article where isdel = 0 and id = #{id});

update office.attachment set 
isdel = 0
where id = (select attachment from office.article where isdel = 0 and id = #{id});
